<script>
import { gsap } from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
import SkillTag from '@/components/SkillTag.vue';

gsap.registerPlugin(ScrollTrigger);

export default {
  name: 'SkillsSection',
  components: {
    SkillTag
  },
  mounted() {
    gsap.from(this.$refs.dot, {
      scrollTrigger: {
        trigger: this.$refs.dot,
        start: "top-=200 center",
        end: "bottom center",
        scrub: true,
      },
      scale: 0,
      ease: "power3.out"
    });

    gsap.fromTo(this.$refs.title, 
      { rotationX: 90, transformOrigin: "top center" }, 
      { 
        rotationX: 0,
        scrollTrigger: {
          trigger: this.$refs.title,
          start: "top 80%",
          end: "top 50%",
          scrub: true,
        },
        ease: "power3.out"
      }
    );

    gsap.fromTo(this.$refs.skillTags.children, 
      { y: 50, opacity: 0 }, 
      { 
        y: 0, 
        opacity: 1, 
        scrollTrigger: {
          trigger: this.$refs.skillTags,
          start: "top 80%",
          end: "top 50%",
          scrub: true,
        },
        ease: "power3.out",
        stagger: 0.2 // Adjust the stagger delay to your preference
      }
    );
  }
}
</script>

